import React from "react";
import gql from "graphql-tag";
import { SINGLE_ITEM_QUERY } from "./SingleProduct";
import { useMutation, useQuery } from "@apollo/client";

const UPDATE_PRODUCT_MUTATION = gql`
	mutation UPDATE_PRODUCT_MUTATION(
		$id: ID!
		$name: String
		$description: String
		$price: Int
	) {
		updateProduct(
			id: $id
			data: { id: $id, name: $name, description: $description, price: $price }
		) {
			id
			name
			description
			price
		}
	}
`;

function UpdateProduct({ id }) {
	// 1. Get the existing product
	const { data, loading, error } = useQuery(SINGLE_ITEM_QUERY, {
		variables: {
			id,
		},
	});
	const {
		Product: { description, name, price },
	} = data;

	// 2. Get the mutation to update the product
	const [
		updateProduct,
		{ data: updateData, error: updateError, loading: updateLoading },
	] = useMutation(UPDATE_PRODUCT_MUTATION, {
		variables: {
			id,
		},
	});

	// 3. Get the form to handle the updates
	return (
		<div>
			<p>Update this product: {name}</p>
		</div>
	);
}

export default UpdateProduct;
