// Toppest level as any component/page may go. If you want something to live in every page, this is the place to do it

import Page from "../components/Page";
import Router from "next/router";
import NProgress from "nprogress";

// TODO : swap with your own
// import "nprogress/nprogress.css";
import "../components/styles/nprogress.css";
import { ApolloProvider } from "@apollo/client";
import withData from "../lib/withData";

Router.events.on("routeChangeStart", () => NProgress.start());
Router.events.on("routeChangeComplete", () => NProgress.done());
Router.events.on("routeChangeError", () => NProgress.done());

function MyApp({ Component, pageProps, apollo }) {
	// console.log(apollo);
	return (
		<ApolloProvider client={apollo}>
			<Page>
				<Component {...pageProps} />
			</Page>
		</ApolloProvider>
	);
}

/*
	If any page have this method, we'll await the fetch request
*/

MyApp.getInitialProps = async function ({ Component, cts }) {
	let pageProps = {};
	if (Component.getInitialProps) {
		pageProps = await Component.getInitialProps(ctx);
	}
	pageProps.query = ctx.query;
	return { pageProps };
};

export default withData(MyApp);
