import React from "react";
import gql from "graphql-tag";
import Form from "./styles/Form";
import { SINGLE_ITEM_QUERY } from "./SingleProduct";
import { useMutation, useQuery } from "@apollo/client";
import DisplayError from "./ErrorMessage";
import useForm from "../lib/useForm";

const UPDATE_PRODUCT_MUTATION = gql`
	mutation UPDATE_PRODUCT_MUTATION(
		$id: ID!
		$name: String
		$description: String
		$price: Int
	) {
		updateProduct(
			id: $id
			data: { id: $id, name: $name, description: $description, price: $price }
		) {
			id
			name
			description
			price
		}
	}
`;

function UpdateProduct({ id }) {
	// 1. Get the existing product
	const { data, loading, error } = useQuery(SINGLE_ITEM_QUERY, {
		variables: {
			id,
		},
	});

	// 2. Get the mutation to update the product
	const [
		updateProduct,
		{ data: updateData, error: updateError, loading: updateLoading },
	] = useMutation(UPDATE_PRODUCT_MUTATION, {
		variables: {
			id,
			// TODO: pass in updates to products here
		},
	});

	// 3. Get the form to handle the updates
	const { inputs, handleChange, resetForm, clearForm } = useForm(data.Product);
	console.log(inputs);

	return (
		<Form
			onSubmit={async (e) => {
				e.preventDefault();
				// Handle submit
			}}
		>
			<DisplayError error={error || updateError} />
			<fieldset disabled={updateLoading} aria-busy={updateLoading}>
				<label htmlFor="name">name</label>
				<input
					type="text"
					id="name"
					name="name"
					placeholder="name"
					value={inputs.name}
					onChange={handleChange}
				/>

				<label htmlFor="name">price</label>
				<input
					type="number"
					id="price"
					name="price"
					placeholder="price"
					value={inputs.price}
					onChange={handleChange}
				/>

				<label htmlFor="name">description</label>
				<textarea
					id="description"
					name="description"
					placeholder="description"
					value={inputs.description}
					onChange={handleChange}
				/>
				<button type="submit">Update product</button>
			</fieldset>
		</Form>
	);
}

export default UpdateProduct;
